import React, { Suspense, useState } from 'react';
import { Canvas, useFrame } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';

const RubiksCubeModel = ({ rotation }) => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');
  scene.scale.set(0.3, 0.3, 0.3);
  scene.rotation.set(Math.PI / 6, Math.PI / 4, 0);

  useFrame(() => {
    // Apply the rotation to the Rubik's Cube
    scene.rotation.set(rotation[0], rotation[1], rotation[2]);
  });

  return <primitive object={scene} />;
};

const RubiksCube = () => {
  // Use the state hook to manage cube rotations
  const [rotation, setRotation] = useState([0, 0, 0]);

  // Function to handle cube rotations based on the set (e.g., top, middle, bottom)
  const rotateCubeSet = (setNumber) => {
    // Define the rotation angles based on the setNumber
    let rotationAngles = [0, 0, 0];
    switch (setNumber) {
      case 1:
        rotationAngles = [Math.PI / 2, 0, 0]; // Rotate the top set
        break;
      // Add cases for other sets

      default:
        break;
    }

    // Update the rotation state to trigger the rotation in useFrame
    setRotation(rotationAngles);
  };

  return (
    <>
    <Canvas>
      <ambientLight />
      <OrbitControls enableZoom={false} />
      <Suspense fallback={null}>
        {/* Pass the rotation state to the RubiksCubeModel */}
        <RubiksCubeModel rotation={rotation} />
      </Suspense>
      <pointLight position={[10, 10, 10]} />


    </Canvas>
    {/* Add buttons or controls to trigger rotations */}
    <button onClick={() => rotateCubeSet(1)}>Rotate Top Set</button>
      {/* Add buttons for other sets */}
    </>

  );
};

export default RubiksCube;
