import React, { useRef } from 'react';
import { useFrame } from 'react-three-fiber';

const Face = ({ position, color, onClick }) => {
  const faceRef = useRef();

  const handlePointerClick = () => {
    onClick();
  };

  useFrame(() => {
    // Rotate the face
    faceRef.current.rotation.x += 0.01;
    faceRef.current.rotation.y += 0.01;
  });

  return (
    <mesh
      position={position}
      onClick={handlePointerClick}
      ref={faceRef}
    >
      <boxGeometry args={[1, 1, 0.01]} />
      <meshBasicMaterial color={color} />
    </mesh>
  );
};

const Box = ({ position }) => {
  return (
    <>
      <Face position={[position[0], position[1], position[2] + 0.5]} color="red" onClick={() => console.log('Clicked face 1')} />
      <Face position={[position[0], position[1], position[2] - 0.5]} color="blue" onClick={() => console.log('Clicked face 2')} />
      {/* Add more faces as needed */}
    </>
  );
};

export default Box;
