import React, { Suspense, useMemo, useState } from "react";
import { Canvas } from "react-three-fiber";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader";
import RubiksCubeModel from "./RubiksCubeModel";
import "./App.css";
import { useLoader } from "react-three-fiber";

const RubiksCube = () => {
  const { scene } = useLoader(GLTFLoader, "/rubikcube.gltf");
  const [drag, setDrag] = useState(true);
  const [upnew, setUpNew] = useState(null);
  const [downnew, setDownNew] = useState(null);
let up;
  const generateNodesSubset = useMemo(() => {
    const subsets = [];

    for (let i = 0; i <= 26; i++) {
      subsets.push(scene.children.slice(i, i + 1));
    }

    return subsets;
  }, [scene]);

  const handleDragChange = (newDrag) => {
    setDrag(newDrag);
  };

  const hadleMouseDown = (down) => {
    if (upnew === null) {
      setDownNew(down);
    }
  };

  const hadleMouseUp = (up) => {
    if (downnew === null) {
      setUpNew(up);
      up=up;
    }

    console.log("downnew ", downnew);
    console.log("upnew ", up);
  };

  return (
    <Canvas>
      <ambientLight />
      <Suspense fallback={null}>
        {generateNodesSubset.map((nodesSubset, index) => (
          <RubiksCubeModel
            index={index}
            key={index}
            nodesSubset={nodesSubset}
            drag={drag}
            onDragChange={handleDragChange}
            mouseDown={hadleMouseDown}
            mouseUp={hadleMouseUp}
            upnew={upnew}
            downnew={downnew}
          />
        ))}
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};
export default RubiksCube;
