      [[16, "Cube000_3"], [17, "Cube001_3"], [18, "Cube002_4"]],
    ],
    // 3
    [
      [[19, "Cube024_4"], [20, "Cube015_3"], [21, "Cube006_3"]],
      [[22, "Cube021_3"], [23, "Cube012_2"], [24, "Cube003_2"]],
      [[25, "Cube018_4"], [26, "Cube009_3"], [27, "Cube000_2"]],
    ],
    // 4
    [
      [[28, "Cube000_1"], [29, "Cube001_2"], [30, "Cube002_3"]],
      [[31, "Cube009_2"], [32, "Cube010_2"], [33, "Cube011_3"]],
      [[34, "Cube018_3"], [35, "Cube019_3"], [36, "Cube020_4"]],
    ],
    // 5
    [
      [[37, "Cube026_2"], [38, "Cube025_2"], [39, "Cube024_2"]],
      [[40, "Cube023_2"], [41, "Cube022_2"], [42, "Cube021_2"]],
      [[43, "Cube020_2"], [44, "Cube019_2"], [45, "Cube018_2"]],
    ],
    // 6
    [
      [[46, "Cube008_3"], [47, "Cube017_3"], [48, "Cube026_4"]],
      [[49, "Cube005_2"], [50, "Cube014_2"], [51, "Cube023_3"]],
      [[52, "Cube002_1"], [53, "Cube011_2"], [54, "Cube020_3"]],
    ],
];

const rotateSet = (setIndex) => {
    const rotatedSet = rubiksCubeMatrixWithIdentifiers[setIndex].map((column) => {
      return column.map(([number, name]) => [number + 9, name]);
    });
console.log(rotatedSet);
    rubiksCubeMatrixWithIdentifiers[setIndex] = rotatedSet;
  };


export const useMouseHandlers = () => {
  const [downnew, setDownNew] = useState(null);
  const [downnewCube, setDownNewCube] = useState(null);

  const handleMouseDown = (down) => {
    setDownNew(down);
  };

  const handleMouseDownCube = (down) => {
    setDownNewCube(down);
  };

  const handleMouseUpCube = (up) => {
    console.log(downnewCube);
    console.log(up);
  };



  const handleMouseUp = (up) => {
    console.log("downnew ", downnew);
    console.log("upnew ", up);


    if (downnew) {
        const nameToSearchDown = downnew;
        const nameToSearchUp = up;
  
        for (let i = 0; i < rubiksCubeMatrixWithIdentifiers.length; i++) {
          for (let j = 0; j < rubiksCubeMatrixWithIdentifiers[i].length; j++) {
            for (let k = 0; k < rubiksCubeMatrixWithIdentifiers[i][j].length; k++) {
              const [number, name] = rubiksCubeMatrixWithIdentifiers[i][j][k];
              if (name === nameToSearchDown) {
                console.log(`Found corresponding number Down: ${number}`);
              }
              if (name === nameToSearchUp) {
                console.log(`Found corresponding number Up: ${number}`);
                rotateSet(i);
              }
            }
          }
        }
    }


  };

  return { handleMouseDown, handleMouseUp,handleMouseDownCube,handleMouseUpCube };
};
