import { useState } from "react";
import { useFrame } from 'react-three-fiber';

function LargeCube() {
  const [hoveredFace, setHoveredFace] = useState(null);

  const handlePointerDown = (event) => {
    console.log("Clicked on face:", hoveredFace);
    if (hoveredFace !== null) {
      // Do something with the clicked face index (hoveredFace)
      console.log("Clicked on face:", hoveredFace);
    }
  };

  return (
    <mesh
      rotation={[Math.PI / 6, Math.PI / 4, 0]}
      onClick={handlePointerDown}
    >
      <boxGeometry attach="geometry" args={[1.82, 1.82, 1.82]} />
      <meshBasicMaterial attachArray="material" color="#00FF00" />
      <meshBasicMaterial attachArray="material" color="#2B00FF" wireframe={true} />
      <meshBasicMaterial attachArray="material" color="#CF8B8B" wireframe={true} />
      <meshBasicMaterial attachArray="material" color="#A9CE4A" wireframe={true} />
      <meshBasicMaterial attachArray="material" color="#11EE0E" wireframe={true} />
      <meshBasicMaterial attachArray="material" color="#DB0DE6" wireframe={true} />
      <meshStandardMaterial attachArray="material" color="#000000" visible={false} />
      <meshStandardMaterial attachArray="material" color="#000000" visible={false} />
      <meshStandardMaterial attachArray="material" color="#000000" visible={false} />
      <meshStandardMaterial attachArray="material" color="#000000" visible={false} />
      <meshStandardMaterial attachArray="material" color="#000000" visible={false} />
      <meshStandardMaterial attachArray="material" color="#000000" visible={false} />

      <meshBasicMaterial attach="material" color="#FFFFFF" wireframe={true} />
    </mesh>
  );
}

export default LargeCube;
