// RubiksCubeRotations.js
import { useRef, useEffect } from "react";
import { useFrame } from "react-three-fiber";

const rotationSpeed = 0.02;

export const useRubiksCubeRotations = (orbitControlsRef, nodesSubset) => {
  const rotatingRef = useRef(false);

  const rotate = (axis, angle) => {
    const quaternion = new THREE.Quaternion().setFromAxisAngle(axis, angle);
    for (const node of nodesSubset) {
      node.applyQuaternion(quaternion);
    }
  };

  const rotateFront = () => {
    if (!rotatingRef.current) {
      rotatingRef.current = true;
      rotate(new THREE.Vector3(0, 0, 1), Math.PI / 2);
      rotatingRef.current = false;
    }
  };

  // Add more rotation functions for other faces (e.g., rotateTop, rotateBottom, etc.)

  useFrame(() => {
    if (orbitControlsRef.current) {
      orbitControlsRef.current.addEventListener("change", () => {
        orbitControlsRef.current.enabled = false;
      });

      useEffect(() => {
        document.addEventListener("keydown", handleKeyPress);
        return () => document.removeEventListener("keydown", handleKeyPress);
      }, []);

      const handleKeyPress = (event) => {
        switch (event.key) {
          case "r":
            rotateFront();
            break;
          // Add more cases for other keys to trigger rotations
          default:
            break;
        }
      };
    }
  });

  return null;
};
