import React, { Suspense, useMemo, useRef, useState, useEffect } from "react";
import { Canvas, useFrame } from "react-three-fiber";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader";
import { useLoader } from "react-three-fiber";
import { OrbitControls } from "@react-three/drei";
import "./App.css";

const RubiksCube = () => {
  const { scene } = useLoader(GLTFLoader, "/rubikcube.gltf");
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  scene.rotation.set(...initialRotation);

  const nodesSubset1 = useMemo(() => scene.children.slice(0, 9), [scene]); //only one layer
  const nodesSubset2 = useMemo(() => scene.children.slice(9), [scene]); //two layers

  const [rotationSubset1, setRotationSubset1] = useState(initialRotation);
  const [rotationSubset2, setRotationSubset2] = useState(initialRotation);

  return (
    <Canvas>
      <ambientLight />
      <OrbitControls enableZoom={false} />
      <Suspense fallback={null}>
        <group position={[0, 0, 0]} rotation={rotationSubset1}>
          <RubiksCubeModel
            nodesSubset={nodesSubset1}
            rotation={rotationSubset1}
            setRotation={setRotationSubset1}
          />
        </group>
        <group position={[0, 0, 0]} rotation={rotationSubset1}>
          <RubiksCubeModel
            nodesSubset={nodesSubset2}
            rotation={rotationSubset2}
            setRotation={setRotationSubset2}
          />
        </group>
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

const RubiksCubeModel = ({ nodesSubset, rotation, setRotation }) => {
  const cubeRefs = useRef([]);
  const startYRef = useRef(null);

    // Store the initial rotation when the mouse is pressed
    const initialRotationRef = useRef(rotation);

    // Store the initial orbit controls rotation when the mouse is pressed
    const initialOrbitRotationRef = useRef([0, 0, 0]);


    const handlePointerDown = (event) => {
      console.log(rotation);
      console.log("down");
      startYRef.current = event.clientY;
      initialRotationRef.current = rotation;
      initialOrbitRotationRef.current = controls.current.getAzimuthalAngle(); // Save initial orbit controls rotation
      window.addEventListener("pointermove", handlePointerMove);
      window.addEventListener("pointerup", handlePointerUp);
      event.stopPropagation();
    };

  let deltaY;

  const handlePointerMove = (event) => {
    console.log("move");
    const startY = startYRef.current;
    if (startY !== null) {
      deltaY = event.clientY - startY;
    }
  };
  let targetRot=0;
  const handlePointerUp = async () => {
    console.log("up");
    window.removeEventListener("pointermove", handlePointerMove);
    window.removeEventListener("pointerup", handlePointerUp);

    // Calculate the target rotation based on the initial rotation, orbit controls rotation, and mouse movement
    const orbitRotation = controls.current.getAzimuthalAngle();
    targetRot = initialRotationRef.current[2] + deltaY * 0.01 - (orbitRotation - initialOrbitRotationRef.current);

    if (deltaY > 0) {
      DraggingDown();
      deltaY = 0;
    } else if (deltaY < 0) {
      DraggingUp();
      deltaY = 0;
    }
  };

  const DraggingDown = async () => {
    cubeRefs.current.forEach(async (cubeRef, index) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z += Math.PI / 30;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  const DraggingUp = async () => {
    cubeRefs.current.forEach(async (cubeRef, index) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z -= Math.PI / 30;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  useFrame(() => {
    // Your animation logic here...
  });

  return (
    <group scale={[0.3, 0.3, 0.3]} onPointerDown={handlePointerDown}>
      {nodesSubset.map((node, index) => (
        <primitive
          key={index}
          object={node}
          ref={(ref) => (cubeRefs.current[index] = { current: ref })}
        />
      ))}
    </group>
  );
};

export default RubiksCube;
