import RubiksCubeModel from "./RubiksCubeModel";


import React, { Suspense, useMemo, useRef, useState, useEffect } from "react";
import { Canvas, useFrame } from "react-three-fiber";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader";
import { useLoader } from "react-three-fiber";


  const nodesSubset1 = useMemo(() => scene.children.slice(0, 9), [scene]); //only one layer
  const nodesSubset2 = useMemo(() => scene.children.slice(9, 18), [scene]); //two layers
  const nodesSubset3 = useMemo(() => scene.children.slice(18, 27), [scene]); //two layers


const CameraController = () => {
  const { scene } = useLoader(GLTFLoader, "/rubikcube.gltf");
  const [rotateCamera, setRotateCamera] = useState(false);

  const handleDocumentClick = (event) => {
    // Replace 'model' with the actual class or id of your model
    if (!event.target.closest('.model')) {
      // Clicked outside the model, enable camera rotation
      setRotateCamera(true);
    } else {
      // Clicked inside the model, disable camera rotation
      setRotateCamera(false);
    }
  };

  useEffect(() => {
    // Attach event listener to the document for clicks
    document.addEventListener('click', handleDocumentClick);

    // Cleanup the event listener when the component unmounts
    return () => {
      document.removeEventListener('click', handleDocumentClick);
    };
  }, []); // Empty dependency array to run the effect once on mount

  const handleCameraRotation = () => {
    if (rotateCamera) {
      // Implement your camera rotation logic here
      console.log('Rotate the camera');
    }
  };

  return (
    <>
     <div>
      <button onClick={handleCameraRotation}>Rotate Camera</button>

    </div>
    <Canvas>
       <ambientLight />
      <Suspense fallback={null}>
         <RubiksCubeModel nodesSubset={nodesSubset1} />
         <RubiksCubeModel nodesSubset={nodesSubset2} />
         <RubiksCubeModel nodesSubset={nodesSubset3} />
       </Suspense>
       <pointLight position={[10, 10, 10]} />
     </Canvas>
    </>

  );
};

export default CameraController;
