const MyScene = () => {
    const orbitref = useRef();
  
    // Access the Three.js scene
    const { gl, camera } = useThree();
  
    useEffect(() => {
      // Disable rotation initially
      orbitref.current.enableRotate = false;
  
      // Function to enable/disable rotation
      const toggleRotation = (enable) => {
        orbitref.current.enableRotate = enable;
        // Force a render update
        gl.render(scene, camera);
      };
  
      // Toggle rotation after a delay (for demonstration purposes)
      setTimeout(() => {
        toggleRotation(true);
      }, 1000);
  
      // Clean up
      return () => {
        // Dispose of controls if needed
        orbitref.current.dispose();
      };
    }, [gl, camera]);
  
    return <orbitControls ref={orbitref} args={[camera, gl.domElement]} />;
  };
  
  export default MyScene;
