// Cube.js
import React, { useRef } from 'react';
import { useFrame } from 'react-three-fiber';

const Cube = ({ position, rotation }) => {
  const cubeRef = useRef();

  useFrame(() => {
    cubeRef.current.rotation.x = rotation[0];
    cubeRef.current.rotation.y = rotation[1];
    cubeRef.current.rotation.z = rotation[2];
  });

  // Define colors for each face
  const faceColors = [0xff0000, 0x00ff00, 0x0000ff, 0xffff00, 0xff8000, 0xffffff];

  return (
    <group position={position}>
      {[...Array(6)].map((_, index) => (
        <mesh key={index} ref={cubeRef}>
          <meshStandardMaterial attachArray="material" color={faceColors[index]} />
          <boxGeometry args={[1, 1, 1]} />
        </mesh>
      ))}
    </group>
  );
};

export default Cube;
