import React, { useState } from 'react';
import { Canvas } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';

const RubiksCube = () => {
  const [cubeState, setCubeState] = useState(createInitialCubeState());

  const rotateFace = (faceIndex, direction) => {
    setCubeState(prevState => {
      const updatedCubeState = [...prevState];

      // Identify the nodes associated with the specified face
      const faceNodes = updatedCubeState[faceIndex];

      // Rotate the cubes in the specified face
      faceNodes.forEach(cube => {
        // Update the cube position based on the direction of rotation
        if (cube && cube.position && Array.isArray(cube.position)) {
          cube.position[0] += direction; // Adjust the position update based on your needs
        }
      });

      return updatedCubeState;
    });
  };


  return (
    <div>
<Canvas>
      <ambientLight />
      <OrbitControls />
      {cubeState.map((face, index) => (
        <Face key={index} cubes={face} />
      ))}
      <pointLight position={[10, 10, 10]} />

      {/* Add buttons for other face rotations */}
    </Canvas>
    <button onClick={() => rotateFace(0, 1)}>Rotate Top Face Clockwise</button>
    </div>

  );
};

const Face = ({ cubes }) => {
  return cubes.map((cube, index) => (
    <Cube key={index} position={cube.position} rotation={cube.rotation} />
  ));
};

const Cube = ({ position, rotation }) => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');

  // Add console logs to check cube and rotation
  console.log('Cube:', position, rotation);

  return <primitive object={scene} position={position} rotation={rotation} />;
};

const createInitialCubeState = () => {
  // Assuming the structure of your model is similar to a 3x3 Rubik's Cube
  const faceNodes = Array.from({ length: 3 }, (_, row) =>
    Array.from({ length: 3 }, (_, col) => ({
      position: [row - 1, col - 1, 0], // Adjust positions based on your needs
      rotation: [0, 0, 0], // Initial rotation
    }))
  );

  // Initialize the state with 6 faces (adjust as needed)
  return Array.from({ length: 6 }, () => faceNodes.map(row => [...row]));
};

export default RubiksCube;
