// Box.js
import React, { useState, useRef } from 'react';
import { useFrame } from 'react-three-fiber';
import * as THREE from 'three';

const Box = ({ position, onClick }) => {
  const [hovered, setHover] = useState(null);
  const boxRef = useRef();

  const handlePointerOver = (index) => {
    setHover(index);
  };

  const handlePointerOut = () => {
    setHover(null);
  };

  const handlePointerClick = (event, index) => {
    event.stopPropagation();
    onClick(index);
  };

  useFrame(() => {
    // Rotate the box
    boxRef.current.rotation.x += 0.01;
    boxRef.current.rotation.y += 0.01;
  });

  return (
    <mesh
      position={position}
      onClick={(event) => handlePointerClick(event, hovered)}
      onPointerOver={() => handlePointerOver(4)} // Change 4 to the face index you want to color
      onPointerOut={handlePointerOut}
      ref={boxRef}
    >
      <boxGeometry args={[1, 1, 1]} />
      <meshBasicMaterial color={hovered === 4 ? 'red' : 'blue'} />
    </mesh>
  );
};

export default Box;
