import { Canvas, useFrame, extend, useThree } from 'react-three-fiber';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import React, { Suspense, useMemo, useRef, useState, useEffect } from "react";
import "./App.css";
import RubiksCubeModel from "./RubiksCubeModel";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader";
import { useLoader } from "react-three-fiber";

// Extend the orbit controls to make it accessible in react-three-fiber
extend({ OrbitControls });

// Custom component to toggle between two orbit controls
const ToggleableOrbitControls = ({ enable, ...props }) => {
  const { camera, gl } = useThree();
  const controlsRef = useRef();

  useFrame(() => {
    controlsRef.current && controlsRef.current.update();
  });

  return enable ? (
    <orbitControls ref={controlsRef} args={[camera, gl.domElement]} {...props} />
  ) : null;
};

// Your main React component
const App = () => {
  const [firstOrbitEnabled, setFirstOrbitEnabled] = useState(true);

  const { scene } = useLoader(GLTFLoader, "/rubikcube.gltf");

    const nodesSubset1 = useMemo(() => scene.children.slice(0, 9), [scene]); //only one layer
    const nodesSubset2 = useMemo(() => scene.children.slice(9, 18), [scene]); //two layers
    const nodesSubset3 = useMemo(() => scene.children.slice(18, 27), [scene]); //two layers


  // Function to toggle between two orbit controls
  const toggleOrbitControls = () => {
    setFirstOrbitEnabled((prev) => !prev);
  };

  return (
    <>
      <Canvas>
      <Suspense fallback={null}>
//         <RubiksCubeModel nodesSubset={nodesSubset1} />
//         <RubiksCubeModel nodesSubset={nodesSubset2} />
//         <RubiksCubeModel nodesSubset={nodesSubset3} />
//       </Suspense>

        {/* Toggleable Orbit Control 1 (with enable=false initially) */}
        <ToggleableOrbitControls enable={!firstOrbitEnabled} />

        {/* Toggleable Orbit Control 2 (with enable=true initially) */}
        <ToggleableOrbitControls enable={firstOrbitEnabled} />
      </Canvas>


      <button onClick={toggleOrbitControls}>Toggle Controls</button>
    </>
  );
};

export default App;
