import React, { Suspense,useRef } from 'react';
import { Canvas ,useFrame} from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';
const RubiksCube = () => {
  return (
    <Canvas>
      <ambientLight />
     <OrbitControls enableZoom={false}/>
     <Suspense fallback={null} >
     <RubiksCubeModel />
     </Suspense>
      <pointLight position={[10, 10, 10]} />

    </Canvas>
  );
};

const RubiksCubeModel = () => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');
  console.log(scene.children[1]);
  scene.scale.set(0.3, 0.3, 0.3); 
  scene.rotation.set(Math.PI / 6, Math.PI / 4, 0);
  // return <primitive object={scene} />;
  const nodesSubset1 = [scene.children[0], scene.children[1], scene.children[2],scene.children[3],scene.children[4],scene.children[5],scene.children[6],scene.children[7],scene.children[8]];
  const nodesSubset2 = [scene.children[9], scene.children[10], scene.children[11],scene.children[12],scene.children[13],scene.children[14],scene.children[15],scene.children[16],scene.children[17],scene.children[18], scene.children[19], scene.children[20],scene.children[21],scene.children[22],scene.children[23],scene.children[24],scene.children[25],scene.children[26]];

  // return <group>{nodesSubset.map((node, index) => <primitive key={index} object={node} />)}</group>;
  return (
    <group>
      <RotatableSet nodesSubset={nodesSubset1} />
      <RotatableSet nodesSubset={nodesSubset2} />
    </group>
  );
};

const RotatableSet = ({ nodesSubset }) => {
  const setGroup = useRef();

  // Use useFrame to apply rotations
  useFrame(() => {
    // Rotate the entire group
    if (setGroup.current) {
      setGroup.current.rotation.y += 0.005; // Adjust rotation speed as needed
    }
  });

  const logPosition = (position) => {
    if (!position || !Array.isArray(position) || position.length !== 3) {
      console.error("Invalid position:", position);
      return [0, 0, 0];
    }

    return position;
  };

  return (
    <group ref={setGroup}>
      {nodesSubset.map((node, index) => (
        <mesh key={index} position={logPosition(node.position)}>
          {node.children && node.children.length > 0 && <primitive object={node.children[0].mesh} />}
        </mesh>
      ))}
    </group>
  );
};

export default RubiksCube;
