import React, { useRef, Suspense } from 'react';
import { Canvas, useThree, extend } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';

// Extend three.js with the OrbitControls
// extend({ OrbitControls });

const RubiksCube = () => {
  return (
    <Canvas>
      <ambientLight />
      <Suspense fallback={null}>
        <RubiksCubeModel />
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

const RotatablePart = () => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');
  const part = useRef();

  // Accessing the first child of the scene
  const firstChild = scene.children[0];

  // Set the rotation and scale for the part you want to rotate
  // firstChild.rotation.set(0, 0, 0);
  // firstChild.scale.set(1, 1, 1);

  scene.scale.set(0.3, 0.3, 0.3); 
  scene.rotation.set(Math.PI / 6, Math.PI / 4, 0);
  // return <primitive ref={part} object={firstChild} />;

  // const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');
  // scene.scale.set(0.3, 0.3, 0.3); 
  // scene.rotation.set(Math.PI / 6, Math.PI / 4, 0);
  return <primitive object={scene} />;
};

const RubiksCubeModel = () => {
  return (
    <>
      <RotatablePart />

      <OrbitControls target={[0, 0, 0]} enableZoom={false}/>
    </>
  );
};

export default RubiksCube;
