import React, { useRef, Suspense, useState } from 'react';
import { Canvas, useFrame, extend } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { OrbitControls } from '@react-three/drei';
import './App.css';

// Extend three.js with the OrbitControls
extend({ OrbitControls });

const RotatablePart = ({ object, enableRotation }) => {
  const part = useRef();

  useFrame(() => {
    if (enableRotation && part.current) {
      part.current.rotation.x += 0.01; // Adjust rotation speed as needed
    }
  });

  // Set the scale for the part you want to rotate
  object.scale.set(1, 1, 1);

  // Conditional rendering based on enableRotation prop
  if (enableRotation) {
    return <primitive ref={part} object={object} />;
  } else {
    return null;
  }
};

const RubiksCube = () => {
  const [isRotating, setRotating] = useState(false);

  const handlePointerMissed = () => {
    setRotating(false);
  };

  return (
    <Canvas
      onPointerMissed={handlePointerMissed}
      onMouseDown={() => setRotating(true)}
      onMouseUp={() => setRotating(false)}
      onTouchStart={() => setRotating(true)}
      onTouchEnd={() => setRotating(false)}
    >
      <ambientLight />
      <Suspense fallback={null}>
        {/* Enable rotation for the first child */}
        <RotatablePart object={scene.children[0]} enableRotation={isRotating} />
        {/* Disable rotation for the second child */}
        <RotatablePart object={scene.children[1]} enableRotation={false} />
      </Suspense>
      <OrbitControls enableZoom={false} />
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

export default RubiksCube;
