import React, { Suspense, useRef } from 'react';
import { Canvas } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';

const RubiksCubeModel = ({ rotateCube }) => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');
  const cubeMeshRef = useRef();

  // Traverse through the nodes in the scene
  scene.traverse((node) => {
    // Check if the node is a mesh and has a numeric name
    if (node.isMesh && !isNaN(node.name)) {
      // Assign a ref to the cube mesh with the specific name
      if (node.name === rotateCube.toString()) {
        cubeMeshRef.current = node;
      }
    }
  });

  // Rotate the cube mesh if it's found
  if (cubeMeshRef.current) {
    cubeMeshRef.current.rotation.x += 0.1;
    cubeMeshRef.current.rotation.y += 0.1;
  }

  return <primitive object={scene} />;
};

const RubiksCube = () => {
  const rotateCube = 0; // Change this to the index of the cube you want to rotate

  return (
    <Canvas>
      <ambientLight />
      <OrbitControls enableZoom={false} />
      <Suspense fallback={null}>
        <RubiksCubeModel rotateCube={rotateCube} />
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

export default RubiksCube;
