import React, { useRef, useEffect,useState } from "react";
import { OrbitControls } from "@react-three/drei";

const RubiksCubeModel = ({ nodesSubset }) => {
  const cubeRefs = useRef([]);
  const startYRef = useRef(0);
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  const groupRef = useRef();

  const [orbitControlsEnabled, setOrbitControlsEnabled] = useState(true);


  const handlePointerDown = (event) => {
    if (event.preventDefault) {
      event.preventDefault();
    }
    if (event.stopPropagation) {
      event.stopPropagation();
    }
    // Disable orbit controls when clicking on the group
    setOrbitControlsEnabled(false);
    console.log("down");
    startYRef.current = event.clientY;
    window.addEventListener("pointermove", handlePointerMove);
    window.addEventListener("pointerup", handlePointerUp);
  };

  let deltaY;

  const handlePointerMove = (event) => {
    console.log("move");
    const startY = startYRef.current;
    if (startY !== null) {
      deltaY = event.clientY - startY;
    }
  };
  let targetRot = 0;
  const handlePointerUp = async () => {
    console.log("up");
    window.removeEventListener("pointermove", handlePointerMove);
    window.removeEventListener("pointerup", handlePointerUp);
    if (deltaY > 0) {
      cubeRefs.current.forEach((cubeRef, index) => {
        targetRot = cubeRef.current.rotation.z + Math.PI / 2;
      });

      DraggingDown();
      setOrbitControlsEnabled(true);
      deltaY = 0;
    } else if (deltaY < 0) {
      cubeRefs.current.forEach((cubeRef, index) => {
        targetRot = cubeRef.current.rotation.z - Math.PI / 2;
        console.log(cubeRef.current.rotation.z);
      });

      DraggingUp();
      setOrbitControlsEnabled(true);
      deltaY = 0;
    }
  };

  const DraggingDown = async () => {
    cubeRefs.current.forEach(async (cubeRef, index) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z += Math.PI / 10;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  const DraggingUp = async () => {
    cubeRefs.current.forEach(async (cubeRef, index) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z -= Math.PI / 10;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  return (
    <>
    <OrbitControls enabled={orbitControlsEnabled}/>
      <group
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
      >
        {nodesSubset.map((node, index) => (
          <primitive
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group>
    </>
  );
};

export default RubiksCubeModel;
