import React, { useRef, useCallback } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame } from "react-three-fiber";

const RubiksCubeModel = ({ index, nodesSubset, drag, onDragChange }) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  const startYRef = useRef(0);
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  const groupRef = useRef();

  const handlePointerDown = useCallback(
    (event) => {
      console.log("Clicked on Node" + index);
      startYRef.current = event.clientY;
      onDragChange(false);
      window.addEventListener("pointermove", handlePointerMove, { passive: true });
      window.addEventListener("pointerup", handlePointerUp, { passive: true });
      event.stopPropagation();
    },
    [index, onDragChange]
  );

  const handlePointerMove = (event) => {
    // handle pointer move logic
  };

  const handlePointerUp = useCallback(
    (event) => {
      console.log("Up on Node" + index);
      window.removeEventListener("pointermove", handlePointerMove);
      window.removeEventListener("pointerup", handlePointerUp);
      onDragChange(true);
      event.stopPropagation();
    },
    [index, onDragChange]
  );

  useFrame(() => {
    orbitRef.current.enabled = drag;
    orbitRef.current.update();
  });

  return (
    <>
      <group
        className="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
        onPointerUp={handlePointerUp}
      >
        {nodesSubset.map((node, index) => (
          <primitive key={index} object={node} ref={(ref) => (cubeRefs.current[index] = { current: ref })} />
        ))}
      </group>
      <group></group>
      <OrbitControls ref={orbitRef} enableZoom={false} />
    </>
  );
};

export default RubiksCubeModel;
