import React, { useRef,useEffect} from "react";
import {useFrame ,useThree} from "react-three-fiber";
import { OrbitControls } from "@react-three/drei";

const RubiksCubeModel = ({ nodesSubset }) => {
    const cubeRefs = useRef([]);
    const startYRef = useRef(0);
    const canvasRef = useRef(null);
    const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
    const { camera, gl } = useThree();
    const orbitControlsRef = useRef();


    useFrame(() => {
      // Update the orbit controls in the animation loop
      orbitControlsRef.current.update();
    });



  const handlePointerDown = (event) => {
    console.log("down");
    startYRef.current = event.clientY;
    window.addEventListener("pointermove", handlePointerMove);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  
};


  
    let deltaY;
  
    
    const handlePointerMove = (event) => {
        console.log("move");
        const startY = startYRef.current;
        if (startY !== null) {
          deltaY = event.clientY - startY;
        }
      
    };
    let targetRot=0;
    const handlePointerUp =async () => {
        console.log("up");
        window.removeEventListener("pointermove", handlePointerMove);
        window.removeEventListener("pointerup", handlePointerUp);
        if (deltaY > 0) {
          cubeRefs.current.forEach((cubeRef, index) => {
            targetRot = cubeRef.current.rotation.z + (Math.PI / 2);
            //console.log(targetRot);
            // console.log(canvas.current.rotation.z);
          });
          
          DraggingDown();
          deltaY = 0;
        } else if (deltaY < 0) {
          cubeRefs.current.forEach((cubeRef, index) => {
            targetRot = cubeRef.current.rotation.z - (Math.PI / 2);
            console.log(cubeRef.current.rotation.z);
          });
          
          DraggingUp();
          deltaY = 0;
        }
      
    };
  
    const DraggingDown = async () => {
      cubeRefs.current.forEach(async (cubeRef, index) => {
        while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
          cubeRef.current.rotation.z += Math.PI / 10;
          await new Promise((resolve) => requestAnimationFrame(resolve));
          await new Promise((resolve) => setTimeout(resolve, 1));
        }
      });
    };
  
    const DraggingUp = async () => {
      cubeRefs.current.forEach(async (cubeRef, index) => {
        while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
          cubeRef.current.rotation.z -= Math.PI / 10;
          await new Promise((resolve) => requestAnimationFrame(resolve));
          await new Promise((resolve) => setTimeout(resolve, 1));
        }
      });
    };
  
    const onDocumentClick = (event) => {
      if (groupRef.current && orbitControlsRef.current) {
        const groupBoundingBox = new THREE.Box3().setFromObject(groupRef.current);
        
        // Check if the click is outside the group bounding box
        if (!groupBoundingBox.containsPoint(new THREE.Vector3(event.clientX, event.clientY, 0))) {
          orbitControlsRef.current.enabled = true;
        } else {
          orbitControlsRef.current.enabled = false;
        }
      }
    };
  
    useEffect(() => {
      document.addEventListener('click', onDocumentClick);
  
      return () => {
        document.removeEventListener('click', onDocumentClick);
      };
    }, []);

    
    return (
      <>
      <OrbitControls ref={orbitControlsRef} enableZoom={false} />
      <group ref={groupRef} scale={[0.3, 0.3, 0.3]} rotation={initialRotation} onPointerDown={handlePointerDown} id={1}>
        {nodesSubset.map((node, index) => (
          <primitive
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group>
      </>
      
    );
  };

  
export default RubiksCubeModel;
