import React, { useEffect, useRef } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame } from "react-three-fiber";
import * as TWEEN from 'tween.js';
import * as THREE from 'three';

const RubiksCubeModel = ({
  index,
  nodesSubset,
  drag,
  onDragChange,
  mouseDownPlane,
  mouseUpPlane,
  mouseDownCube,
  set,
  xyzdirection,
  direction,
}) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  const initialRotation = [Math.PI / 6, Math.PI/4, 0];
  // const initialRotation = [0, 0, 0];
  const groupRef = useRef();
  let rotationindex;

  let checkup;
  const handlePointerDown = (event) => {
    const clickedPlane = groupRef.current.children[0].children.find(
      (child) => child === event.object
    );
    if (clickedPlane) {
      mouseDownPlane(clickedPlane.name);
      mouseDownCube(index);
    }
    checkup = 1;
    onDragChange(false);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  };

  useEffect(() => {
    rotationindex = set;
    Dragging(rotationindex);
  }, [set]);

  const handlePointerUp = async (event) => {
    if (checkup == null) {
      const clickedPlane = groupRef.current.children[0].children.find(
        (child) => child === event.object
      );
      if (clickedPlane) {
        mouseUpPlane(clickedPlane.name);
      }
      window.removeEventListener("pointerup", handlePointerUp);
      event.stopPropagation();
    }
    onDragChange(true);
  };

  const Dragging = async (cubeIndices) => {
    if (cubeIndices != null) {
      if (cubeIndices.includes(index)) {
        if (direction === "a") {
          Up();  
        } else if(direction === "c"){
          Down();
          console.log("down");
        }
      }
    }
  };
  const animateRotation = (target, endRotation, duration) => {
    return new Promise((resolve) => {
      new TWEEN.Tween(target.rotation)
        .to(endRotation, duration)
        .onComplete(() => resolve())
        .start();
    });
  };

  const rotateGroupAroundWorldX = async (radians) => {
    // Rotate back to the original state
    const inverseInitialRotation = new THREE.Euler().setFromRotationMatrix(groupRef.current.matrixWorld).negate();
    await animateRotation(groupRef.current, {
      x: inverseInitialRotation.x,
      y: inverseInitialRotation.y,
      z: inverseInitialRotation.z,
    }, 100);

    // Rotate around the world X-axis
    const axis = new THREE.Vector3(1, 0, 0);
    const quaternion = new THREE.Quaternion().setFromAxisAngle(axis, radians);
    groupRef.current.setRotationFromQuaternion(quaternion);

    // Update the initial rotation for future reference
    initialRotation[0] += radians;

    // Apply the new rotation
    await animateRotation(groupRef.current, {
      x: initialRotation[0],
      y: initialRotation[1],
      z: initialRotation[2],
    }, 100);
  };

  const Up = async () => {
    const radians = -Math.PI / 2; // Rotate 90 degrees
    await rotateGroupAroundWorldX(radians);
  };

  const Down = async () => {
    const radians = Math.PI / 2; // Rotate -90 degrees
    await rotateGroupAroundWorldX(radians);
  };


  return (
    <>
      <group
        className="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
        onPointerUp={handlePointerUp}
        position={[0, 0, 0]}
      >
        {nodesSubset.map((node, nodeIndex) => (
          <primitive
            key={nodeIndex}
            object={node}
            ref={(ref) => (cubeRefs.current[nodeIndex] = ref)} 
          />
        ))}
      </group>
      <OrbitControls ref={orbitRef} enableZoom={false} />
      <axesHelper args={[5]} />
    </>
  );
};

export default RubiksCubeModel;
