import React, { Suspense, useMemo, useRef, useEffect } from 'react';
import { Canvas, useThree } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader, useFrame } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';
// import { PointerLockControls } from 'three/examples/jsm/PointerLockControls'; // Import PointerLockControls from the correct path
const RubiksCube = () => {
  const { scene, camera } = useThree();

  const { nodesSubset1, nodesSubset2 } = useRubiksCubeParts(scene);

  return (
    <Canvas>
      <ambientLight />
      <OrbitControls enableZoom={false} />
      <PointerLockControls />
      <Suspense fallback={null}>
        <group position={[2, 0, 0]}>
          <RubiksCubeModel nodesSubset={nodesSubset1} />
        </group>
        <group position={[2, 0, 0]}>
          <RubiksCubeModel nodesSubset={nodesSubset2} />
        </group>
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

const useRubiksCubeParts = (scene) => {
  const nodesSubset1 = useMemo(() => scene.children.slice(0, 9), [scene]);
  const nodesSubset2 = useMemo(() => scene.children.slice(9), [scene]);

  return { nodesSubset1, nodesSubset2 };
};

const RubiksCubeModel = ({ nodesSubset }) => {
  const groupRef = useRef();

  // Rotate the group around its center
  useFrame(({ mouse }) => {
    if (groupRef.current) {
      groupRef.current.rotation.x = mouse.y * 0.5;
      groupRef.current.rotation.y = mouse.x * 0.5;
    }
  });

  return (
    <group ref={groupRef} scale={[0.2, 0.2, 0.2]}>
      {nodesSubset.map((node, index) => (
        <primitive key={index} object={node} />
      ))}
    </group>
  );
};

const PointerLockControls = () => {
  const { camera, gl } = useThree();
  const controls = useRef();

  useEffect(() => {
    const pointerLockControls = new THREE.PointerLockControls(camera, gl.domElement);
    controls.current = pointerLockControls;
    camera.add(pointerLockControls.getObject());
    pointerLockControls.addEventListener('lock', () => {});
    pointerLockControls.addEventListener('unlock', () => {});
    document.addEventListener('keydown', (event) => {
      if (event.code === 'KeyF') {
        pointerLockControls.lock();
      }
    });
    return () => {
      document.removeEventListener('keydown', (event) => {
        if (event.code === 'KeyF') {
          pointerLockControls.unlock();
        }
      });
    };
  }, [camera, gl]);

  useFrame(() => {
    if (controls.current) {
      controls.current.update();
    }
  });

  return null;
};

export default RubiksCube;
