import React, { useRef, useState } from 'react';
import { Canvas, useThree, useFrame } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';

const CubeGroup = React.forwardRef(({ children, rotationEnabled }, ref) => {
  // Disable rotation when rotation is not enabled
  useFrame(() => {
    if (rotationEnabled && ref.current) {
      ref.current.rotation.x += 0.01;
      ref.current.rotation.y += 0.01;
    }
  });

  return <group ref={ref}>{children}</group>;
});

const RubiksCubeModel = ({ setsRotations }) => {
  const { scene } = useThree();
  scene.scale.set(0.3, 0.3, 0.3);

  // Create an array of refs for each set (cube)
  const cubeRefs = Array.from({ length: 27 }, () => useRef());

  // Apply rotations to each set
  useFrame(() => {
    setsRotations.forEach((rotation, setIndex) => {
      const cubeRef = cubeRefs[setIndex];

      if (cubeRef.current) {
        cubeRef.current.rotation.set(rotation[0], rotation[1], rotation[2]);
      }
    });
  });

  return (
    <>
      {cubeRefs.map((cubeRef, index) => (
        <CubeGroup key={index} rotationEnabled={setsRotations.length === 0} ref={cubeRef}>
          {/* Include your cube objects here */}
          {/* Example: <mesh position={[0, 0, 0]}><boxGeometry /><meshBasicMaterial color="red" /></mesh> */}
        </CubeGroup>
      ))}

      <OrbitControls enableZoom={false} />
    </>
  );
};

const RubiksCube = () => {
  const [setsRotations, setSetsRotations] = useState([]);

  // Function to handle set rotations
  const rotateSet = () => {
    const newRotation = [Math.PI / 2, 0, 0]; // Replace with your rotation logic

    // Update setsRotations state
    setSetsRotations([...setsRotations, newRotation]);
  };

  return (
    <Canvas>
      <ambientLight />
      <RubiksCubeModel setsRotations={setsRotations} />
      <pointLight position={[10, 10, 10]} />

      <button onClick={rotateSet}>Rotate Set</button>
    </Canvas>
  );
};

export default RubiksCube;
