import React, { createContext, useContext, useState } from "react";

const MouseEventsContext = createContext();

export const useMouseEvents = () => {
  const context = useContext(MouseEventsContext);
  if (!context) {
    throw new Error("useMouseEvents must be used within a MouseEventsProvider");
  }
  return context;
};

export const MouseEventsProvider = ({ children }) => {
  const [downEvent, setDownEvent] = useState(null);
  const [upEvent, setUpEvent] = useState(null);

  const setMouseEvents = (down, up) => {
    setDownEvent(down);
    setUpEvent(up);
  };

  return (
    <MouseEventsContext.Provider value={{ downEvent, upEvent, setMouseEvents }}>
      {children}
    </MouseEventsContext.Provider>
  );
};
