import React, { useEffect, useRef } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame } from "react-three-fiber";
import * as THREE from 'three';
import { Tween } from 'tween.js';
import * as TWEEN from 'tween.js';


const RubiksCubeModel = ({
  index,
  nodesSubset,
  drag,
  onDragChange,
  mouseDownPlane,
  mouseUpPlane,
  mouseDownCube,
  set,
  xyzdirection,
  direction,
  pivotGroup
}) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  // const initialRotation = [Math.PI / 6, Math.PI/4, 0];
  const initialRotation = [0, 0, 0];
  const groupRef = useRef();
  let rotationindex;

  let checkup;
  const handlePointerDown = (event) => {
    const clickedPlane = groupRef.current.children[0].children.find(
      (child) => child === event.object
    );
    if (clickedPlane) {
      mouseDownPlane(clickedPlane.name);
      mouseDownCube(index);
    }
    checkup = 1;
    onDragChange(false);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  };

  useEffect(() => {
    rotationindex = set;
    Dragging(rotationindex);
  }, [set]);

  const handlePointerUp = async (event) => {
    if (checkup == null) {
      const clickedPlane = groupRef.current.children[0].children.find(
        (child) => child === event.object
      );
      if (clickedPlane) {
        mouseUpPlane(clickedPlane.name);
      }
      window.removeEventListener("pointerup", handlePointerUp);
      event.stopPropagation();
    }
    onDragChange(true);
  };

  let targetRot;
  const Dragging = async (cubeIndices) => {
    if (cubeIndices != null) {
      if (cubeIndices.includes(index)) {
        if (direction === "a") {
          if (xyzdirection === "x") {
            targetRot = groupRef.current.children[0].rotation.x - Math.PI / 2;           
          } else if (xyzdirection === "y") {
            targetRot = groupRef.current.children[0].rotation.y - Math.PI / 2;
          } else if (xyzdirection === "z") {
            targetRot = groupRef.current.children[0].rotation.z - Math.PI / 2;
          }
          // Up();
          console.log(groupRef.current.children[0].rotation);
          
        } else if(direction === "c"){
          if (xyzdirection === "x") {
            targetRot = groupRef.current.children[0].rotation.x + Math.PI / 2;
          } else if (xyzdirection === "y") {
            targetRot = groupRef.current.children[0].rotation.y + Math.PI / 2;
          } else if (xyzdirection === "z") {
            targetRot = groupRef.current.children[0].rotation.z + Math.PI / 2;
          }
          Down();
          console.log("down");
        }
      }
    }
  };


  // const animateRotation = (target, endRotation, duration) => {
  //   return new Promise((resolve) => {
  //     new TWEEN.Tween(target.rotation)
  //       .to(endRotation, duration)
  //       .onComplete(() => resolve())
  //       .start();
  //   });
  // };

  function rotate(cubeGroup, rotationGroup, axis, limit, multiplier) {
    if (!TWEEN.getAll().length) {
      resetCubeGroup(cubeGroup, rotationGroup)
      attachToRotationGroup(cubeGroup, rotationGroup, axis, limit)
      animateRotationGroup(rotationGroup, axis, multiplier)
    }
  }

  // const Up = async () => {
  //   console.log("up");
  //   if (xyzdirection === "x") {
  //     for (let i = 0; i < 15; i++) {
  //       await animateRotation(groupRef.current, { x: groupRef.current.rotation.x - Math.PI / 30, y: groupRef.current.rotation.y, z: groupRef.current.rotation.z }, 100);
  //       console.log(i);
  //     }
  //   } else if (xyzdirection === "y") {
  //     for (let i = 0; i < 15; i++) {
  //       await animateRotation(groupRef.current, { x: groupRef.current.rotation.x, y: groupRef.current.rotation.y - Math.PI / 30, z: groupRef.current.rotation.z }, 100);
  //     }
  //   } else if (xyzdirection === "z") {
  //     for (let i = 0; i < 15; i++) {
  //       await animateRotation(groupRef.current, { x: groupRef.current.rotation.x, y: groupRef.current.rotation.y, z: groupRef.current.rotation.z - Math.PI / 30 }, 100);
  //     }
  //   }
  // };



  const Down = async () => {
    if (xyzdirection === "x") {
      for (let i = 0; i < 15; i++) {
        rotate(cubeRefs.current, groupRef.current, 'x', -0.5, 1)
        // await animateRotation(groupRef.current, { x: groupRef.current.rotation.x + Math.PI / 30, y: groupRef.current.rotation.y, z: groupRef.current.rotation.z }, 100);
        console.log(i);
      }
      console.log(groupRef.current.rotation);
    } else if (xyzdirection === "y") {
      for (let i = 0; i < 15; i++) {
        await animateRotation(groupRef.current, { x: groupRef.current.rotation.x, y: groupRef.current.rotation.y + Math.PI / 30, z: groupRef.current.rotation.z }, 100);
      }
    } else if (xyzdirection === "z") {
      for (let i = 0; i < 15; i++) {
        await animateRotation(groupRef.current, { x: groupRef.current.rotation.x, y: groupRef.current.rotation.y, z: groupRef.current.rotation.z + Math.PI / 30 }, 100);
      }
    }
  };
  
  
  useFrame(() => {
    orbitRef.current.enabled = drag;
    orbitRef.current.update();
    TWEEN.update()
  });

  return (
    <>
      <group
        className="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
        onPointerUp={handlePointerUp}
      >
        {nodesSubset.map((node, nodeIndex) => (
          <primitive
            key={nodeIndex}
            object={node}
            ref={(ref) => (cubeRefs.current[nodeIndex] = ref)} 
          />
        ))}
      </group>
      <group></group>
      <OrbitControls ref={orbitRef} enableZoom={false} />
      <axesHelper args={[5]} />
    </>
  );
};

export default RubiksCubeModel;
