import React, { useRef } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame } from "react-three-fiber";

const RubiksCubeModel = ({ index, nodesSubset, drag, onDragChange,mouseDownPlane,mouseUpPlane,mouseDownCube,mouseUpCube,rotateset }) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  const groupRef = useRef();

  
  let checkup;
  const handlePointerDown = (event) => {
   

    const clickedPlane = groupRef.current.children[0].children.find(child => child === event.object);
    if (clickedPlane) {
      mouseDownPlane(clickedPlane.name);
      mouseDownCube(index);
    }
    checkup = 1;
    onDragChange(false);
    window.addEventListener("pointermove", handlePointerMove);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  };

  const handlePointerMove = (event) => {};

  const handlePointerUp = async (event) => {

    if (checkup == null) {
      
      const clickedPlane = groupRef.current.children[0].children.find(child => child === event.object);
    if (clickedPlane) {
      mouseUpPlane(clickedPlane.name);
      mouseUpCube(index);
      DraggingDown(rotateset);
    }
      window.removeEventListener("pointermove", handlePointerMove);
      window.removeEventListener("pointerup", handlePointerUp);
      event.stopPropagation();
    }
    
    onDragChange(true);
    
   
  };

  let targetRot;
  cubeRefs.current.forEach((cubeRef, index) => {
    targetRot = cubeRef.current.rotation.z + (Math.PI / 2);
  });

  const DraggingDown = async (cubeIndices) => {
    cubeIndices.forEach(async (index) => {
      const cubeRef = cubeRefs.current[index];
  
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z += Math.PI / 30;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  useFrame(() => {
    orbitRef.current.enabled = drag;
    orbitRef.current.update();
  });

  return (
    <>
      <group
        classname="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
        onPointerUp={handlePointerUp}
      >
        {nodesSubset.map((node, index) => (
          <primitive
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group>
      <group></group>
      <OrbitControls ref={orbitRef} enableZoom={false} />
    </>
  );
};

export default RubiksCubeModel;
