import React, { Suspense, useMemo, useState } from "react";
import { Canvas } from "react-three-fiber";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader";
import RubiksCubeModel from "./RubiksCubeModel";
import "./App.css";
import { useLoader } from "react-three-fiber";
const RubiksCube = () => {
  const { scene } = useLoader(GLTFLoader, "/rubikcube.gltf");
  const [drag, setDrag] = useState(true);

  const nodesSubset0 = useMemo(() => scene.children.slice(0, 1), [scene]);
  const nodesSubset1 = useMemo(() => scene.children.slice(1, 2), [scene]);
  const nodesSubset2 = useMemo(() => scene.children.slice(2, 3), [scene]);
  const nodesSubset3 = useMemo(() => scene.children.slice(3, 4), [scene]);
  const nodesSubset4 = useMemo(() => scene.children.slice(4, 5), [scene]);
  const nodesSubset5 = useMemo(() => scene.children.slice(5, 6), [scene]);
  const nodesSubset6 = useMemo(() => scene.children.slice(6, 7), [scene]);
  const nodesSubset7 = useMemo(() => scene.children.slice(7, 8), [scene]);
  const nodesSubset8 = useMemo(() => scene.children.slice(8, 9), [scene]);
  const nodesSubset9 = useMemo(() => scene.children.slice(9, 10), [scene]);
  const nodesSubset10 = useMemo(() => scene.children.slice(10, 11), [scene]);
  const nodesSubset11 = useMemo(() => scene.children.slice(11, 12), [scene]);
  const nodesSubset12 = useMemo(() => scene.children.slice(12, 13), [scene]);
  const nodesSubset13 = useMemo(() => scene.children.slice(13, 14), [scene]);
  const nodesSubset14 = useMemo(() => scene.children.slice(14, 15), [scene]);
  const nodesSubset15 = useMemo(() => scene.children.slice(15, 16), [scene]);
  const nodesSubset16 = useMemo(() => scene.children.slice(16, 17), [scene]);
  const nodesSubset17 = useMemo(() => scene.children.slice(17, 18), [scene]);
  const nodesSubset18 = useMemo(() => scene.children.slice(18, 19), [scene]);
  const nodesSubset19 = useMemo(() => scene.children.slice(19, 20), [scene]);
  const nodesSubset20 = useMemo(() => scene.children.slice(20, 21), [scene]);
  const nodesSubset21 = useMemo(() => scene.children.slice(21, 22), [scene]);
  const nodesSubset22 = useMemo(() => scene.children.slice(22, 23), [scene]);
  const nodesSubset23 = useMemo(() => scene.children.slice(23, 24), [scene]);
  const nodesSubset24 = useMemo(() => scene.children.slice(24, 25), [scene]);
  const nodesSubset25 = useMemo(() => scene.children.slice(25, 26), [scene]);
  const nodesSubset26 = useMemo(() => scene.children.slice(26,27), [scene]);



  const handleDragChange = (newDrag) => {
    setDrag(newDrag);
  };


  const renderRubiksCubeModels = () => {
    const cubeModels = [];

    for (let i = 0; i <= 26; i++) {
      const nodesSubset = useMemo(() => scene.children.slice(i, i + 1), [scene]);
      cubeModels.push(
        <RubiksCubeModel
          key={i} // Don't forget to add a unique key for each component when using map or loop
          nodesSubset={nodesSubset}
          drag={drag}
          onDragChange={handleDragChange}
        />
      );
    }

    return cubeModels;
  };

  return (
    <Canvas>
      <ambientLight />
      <Suspense fallback={null}>
      {renderRubiksCubeModels()}
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

export default RubiksCube;
