import React, { useRef,useState } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame } from "react-three-fiber";

const RubiksCubeModel = ({ index, nodesSubset, drag, onDragChange,mouseDown,mouseUp }) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  const groupRef = useRef();

  const rubiksCubeMatrix = [
    // 1 layer
    [
      ["B", "B", "B"],
      ["B", "B", "B"],
      ["B", "B", "B"],
    ],
    // 2
    [
      ["W", "W", "W"],
      ["W", "W", "W"],
      ["W", "W", "W"],
    ],
    // 3
    [
      ["R", "R", "R"],
      ["R", "R", "R"],
      ["R", "R", "R"],
    ],
    // 4
    [
      ["G", "G", "G"],
      ["G", "G", "G"],
      ["G", "G", "G"],
    ],
    //5
    [
      ["Y", "Y", "Y"],
      ["Y", "Y", "Y"],
      ["Y", "Y", "Y"],
    ],
    // 6
    [
      ["O", "O", "O"],
      ["O", "O", "O"],
      ["O", "O", "O"],
    ],
  ];

  let checkup;
  const handlePointerDown = (event) => {
   

    const clickedPlane = groupRef.current.children[0].children.find(child => child === event.object);
    if (clickedPlane) {
      mouseDown(clickedPlane.name);
    }
    checkup = 1;
    onDragChange(false);
    window.addEventListener("pointermove", handlePointerMove);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  };

  const handlePointerMove = (event) => {};

  const handlePointerUp = async (event) => {

    if (checkup == null) {
      
      const clickedPlane = groupRef.current.children[0].children.find(child => child === event.object);
    if (clickedPlane) {
      mouseUp(clickedPlane.name);
      // console.log("down Plane:",downnew);
      // console.log("up Plane:", clickedPlane.name);
    }
      window.removeEventListener("pointermove", handlePointerMove);
      window.removeEventListener("pointerup", handlePointerUp);
      event.stopPropagation();
    }
    
    onDragChange(true);
    
   
  };

  useFrame(() => {
    orbitRef.current.enabled = drag;
    orbitRef.current.update();
  });

  return (
    <>
      <group
        classname="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
        onPointerUp={handlePointerUp}
      >
        {nodesSubset.map((node, index) => (
          <primitive
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group>
      <group></group>
      <OrbitControls ref={orbitRef} enableZoom={false} />
    </>
  );
};

export default RubiksCubeModel;
