import { useRef } from "react";
import { useThree } from "react-three-fiber";
import * as THREE from 'three';

function LargeCube() {
  const meshRef = useRef();
  const { camera, raycaster, mouse } = useThree();

  const handleClick = (event) => {
    // Calculate mouse position in normalized device coordinates
    const { clientX, clientY } = event;
    const x = (clientX / window.innerWidth) * 2 - 1;
    const y = -(clientY / window.innerHeight) * 2 + 1;

    // Set mouse position for raycaster
    mouse.set(x, y);

    // Raycast from the camera to the clicked point
    raycaster.setFromCamera(mouse, camera);

    // Check for intersections with the cube
    const intersects = raycaster.intersectObject(meshRef.current);

    if (intersects.length > 0) {
      const faceIndex = intersects[0].faceIndex;
      // Handle the click on the face with index faceIndex
      console.log("Clicked on face:", faceIndex);
    }
  };

  return (
    <mesh
      ref={meshRef}
      rotation={[Math.PI / 6, Math.PI / 4, 0]}
      onClick={handleClick}
    >
      <boxGeometry attach="geometry" args={[1.8, 1.8, 1.8]} />
      <meshStandardMaterial attach="material" color="#6be092" wireframe={true} />
    </mesh>
  );
}

export default LargeCube;
