import React, { useRef, useEffect, useState } from "react";
// import { OrbitControls } from "@react-three/drei";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";
import { Canvas, useThree } from "react-three-fiber";
import * as THREE from "three";
import { CameraControls } from "@react-three/drei";

const RubiksCubeModel = ({ nodesSubset }) => {

  const cubeRefs = useRef([]);
  const startYRef = useRef(0);
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  const groupRef = useRef();
  const orbit = useRef();
  const [lockOrbitControls, setLockOrbitControls] = useState(true);
let a=1;
  let controls;
  const { camera, gl } = useThree();
  // const CameraController = () => {
  
    
  //   useEffect(
  //     () => {
  //       controls = new OrbitControls(camera, gl.domElement,lockOrbitControls);
  //       if(a==0){
  //         controls.enabled=false;
  //         console.log("okkkkkkkkkkkkkk");
  //       }
  //       controls.minDistance = 3;
  //       controls.maxDistance = 20;
  //       return () => {
  //         controls.dispose();
  //       };
  //     },
  //     [camera, gl,lockOrbitControls]
  //   );
  //   return null;
  // };
  // const cameraControlRef = useRef<CameraControls | null>(null);

  const handlePointerDown = (event) => {

    // setLockOrbitControls({ false}, () => {
    //   console.log(this.state.dealersOverallTotal, 'dealersOverallTotal1');
    // }); 

    
    // if (controls) {
    //   controls.enabled = false;
    //   controls.dispose();
    //   controls.update();
    // }

    console.log("down");
    startYRef.current = event.clientY;
    if (event.target.tagName === "CANVAS") {
      console.log("outside true");
    } else {
      console.log("inside false");
    }

    window.addEventListener("pointermove", handlePointerMove);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  };

  let deltaY;

  const handlePointerMove = (event) => {
    console.log("move");
    const startY = startYRef.current;
    if (startY !== null) {
      deltaY = event.clientY - startY;
    }
  };

  let targetRot = 0;

  const handlePointerUp = async () => {
    console.log("up");
    window.removeEventListener("pointermove", handlePointerMove);
    window.removeEventListener("pointerup", handlePointerUp);
    if (deltaY > 0) {
      cubeRefs.current.forEach((cubeRef, index) => {
        targetRot = cubeRef.current.rotation.z + Math.PI / 2;
      });

      DraggingDown();
      // controls.enableRotate = true;
      deltaY = 0;
    } else if (deltaY < 0) {
      cubeRefs.current.forEach((cubeRef, index) => {
        targetRot = cubeRef.current.rotation.z - Math.PI / 2;
        console.log(cubeRef.current.rotation.z);
      });

      DraggingUp();
      // controls.enableRotate = true;
      deltaY = 0;
    }
  };

  const DraggingDown = async () => {
    cubeRefs.current.forEach(async (cubeRef, index) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z += Math.PI / 10;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  const DraggingUp = async () => {
    cubeRefs.current.forEach(async (cubeRef, index) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z -= Math.PI / 10;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  return (
    <>

      <group
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
      >
        {nodesSubset.map((node, index) => (
          <primitive
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group>
      <OrbitControls ref={orbit}/>
    </>
  );
};

export default RubiksCubeModel;
