import React, { Suspense, useRef, useState } from 'react';
import { Canvas, useFrame } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import './App.css';

const RotatablePart = ({ object }) => {
  const part = useRef();
  const [dragging, setDragging] = useState(false);
  const [previousMouseX, setPreviousMouseX] = useState(0);

  const handleMouseDown = (event) => {
    setDragging(true);
    setPreviousMouseX(event.clientX);
  };

  const handleMouseUp = () => {
    setDragging(false);
  };

  const handleMouseMove = (event) => {
    if (dragging && part.current) {
      const deltaX = event.clientX - previousMouseX;
      part.current.rotation.y += deltaX * 0.005; // Adjust rotation speed as needed
      setPreviousMouseX(event.clientX);
    }
  };

  return (
    <group ref={part} onClick={handleMouseDown} onPointerUp={handleMouseUp} onPointerMove={handleMouseMove}>
      {object}
    </group>
  );
};

const RubiksCubeModel = () => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');

  // Check if scene or nodes is not defined
  if (!scene || !scene.nodes) {
    console.error('Model not loaded correctly.');
    return null;
  }

  scene.scale.set(0.3, 0.3, 0.3);
  scene.rotation.set(Math.PI / 6, Math.PI / 4, 0);

  return (
    <group>
      {scene.nodes.map((node, index) => (
        <RotatablePart key={index} object={scene.children[node.mesh]} />
      ))}
    </group>
  );
};

const RubiksCube = () => {
  return (
    <Canvas>
      <ambientLight />
      <Suspense fallback={null}>
        <RubiksCubeModel />
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

export default RubiksCube;
