import React, { useState } from 'react';
import RubikCube from './Cube';

const App = () => {
  const [rotation, setRotation] = useState(0);

  const handleRotateX = () => {
    setRotation(rotation + Math.PI / 2); // Rotate by 90 degrees
  };

  return (
    <div>
      <RubikCube rotation={[rotation, 0, 0]} />
      <button onClick={handleRotateX}>Rotate X</button>
    </div>
  );
};

export default App;
