import React, { useEffect, useRef } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame } from "react-three-fiber";
import * as TWEEN from "tween.js";

const RubiksCubeModel = ({
  index,
  nodesSubset,
  drag,
  onDragChange,
  mouseDownPlane,
  mouseUpPlane,
  mouseDownCube,
  set,
  xyzdirection,
  direction,
}) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  const groupRef = useRef();

  useEffect(() => {
    const cubesInSet = set || [];
    cubesInSet.forEach((cubeIndex) => {
      if (cubeRefs.current[cubeIndex]) {
        groupRef.current.add(cubeRefs.current[cubeIndex]);
      }
    });
  }, [set]);

  const handlePointerDown = (event) => {
    const clickedPlane = groupRef.current.children.find(
      (child) => child === event.object
    );

    if (clickedPlane) {
      mouseDownPlane(clickedPlane.name);
      mouseDownCube(index);
    }

    onDragChange(false);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  };

  const handlePointerUp = (event) => {
    const clickedPlane = groupRef.current.children.find(
      (child) => child === event.object
    );

    if (clickedPlane) {
      mouseUpPlane(clickedPlane.name);
    }

    window.removeEventListener("pointerup", handlePointerUp);
    onDragChange(true);
  };

  const rotateGroup = async (rotationAxis, radians) => {
    const initialRotation = groupRef.current.rotation.clone();

    for (let i = 0; i < 15; i++) {
      initialRotation[rotationAxis] += radians / 15;
      await animateRotation(groupRef.current, {
        x: initialRotation.x,
        y: initialRotation.y,
        z: initialRotation.z,
      }, 100);
    }
  };

  const Up = async () => {
    if (xyzdirection === "x") {
      await rotateGroup(0, -Math.PI / 2);
    } else if (xyzdirection === "y") {
      await rotateGroup(1, -Math.PI / 2);
    } else if (xyzdirection === "z") {
      await rotateGroup(2, -Math.PI / 2);
    }
  };

  const Down = async () => {
    if (xyzdirection === "x") {
      await rotateGroup(0, Math.PI / 2);
    } else if (xyzdirection === "y") {
      await rotateGroup(1, Math.PI / 2);
    } else if (xyzdirection === "z") {
      await rotateGroup(2, Math.PI / 2);
    }
  };

  const animateRotation = (target, endRotation, duration) => {
    return new Promise((resolve) => {
      new TWEEN.Tween(target.rotation)
        .to(endRotation, duration)
        .onComplete(() => resolve())
        .start();
    });
  };

  useFrame(() => {
    orbitRef.current.enabled = drag;
    orbitRef.current.update();
    TWEEN.update();
  });

  return (
    <>
      <group
        className="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={[0, 0, 0]}
        onPointerDown={handlePointerDown}
        onPointerUp={handlePointerUp}
      >
        {nodesSubset.map((node, nodeIndex) => (
          <primitive
            key={nodeIndex}
            object={node}
            ref={(ref) => (cubeRefs.current[nodeIndex] = ref)}
          />
        ))}
      </group>
      <OrbitControls ref={orbitRef} enableZoom={false} />
      <axesHelper args={[5]} />
    </>
  );
};

export default RubiksCubeModel;
