import React, { useRef,useState} from "react";

const RubiksCubeModel = ({ nodesSubset }) => {
    const cubeRefs = useRef([]);
    const startYRef = useRef(0);
    const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
    const groupRef = useRef();


  const handlePointerDown = (event) => {
    if (event.preventDefault) {
      event.preventDefault();
  }
  if (event.stopPropagation) {
      event.stopPropagation();
  }
    console.log("down");
    startYRef.current = event.clientY;
    window.addEventListener("pointermove", handlePointerMove);
    window.addEventListener("pointerup", handlePointerUp);

  
};


  
    let deltaY;
  
    
    const handlePointerMove = (event) => {
        console.log("move");
        const startY = startYRef.current;
        if (startY !== null) {
          deltaY = event.clientY - startY;
        }
      
    };
    let targetRot=0;
    const handlePointerUp =async () => {
        console.log("up");
        window.removeEventListener("pointermove", handlePointerMove);
        window.removeEventListener("pointerup", handlePointerUp);
        if (deltaY > 0) {
          cubeRefs.current.forEach((cubeRef, index) => {
            targetRot = cubeRef.current.rotation.z + (Math.PI / 2);
          });
          
          DraggingDown();
          deltaY = 0;
          // setOrbitControlsEnabled(true);
        } else if (deltaY < 0) {
          cubeRefs.current.forEach((cubeRef, index) => {
            targetRot = cubeRef.current.rotation.z - (Math.PI / 2);
            console.log(cubeRef.current.rotation.z);
          });
          
          DraggingUp();
          deltaY = 0;
          // setOrbitControlsEnabled(true);
        }
      
    };
  
    const DraggingDown = async () => {
      cubeRefs.current.forEach(async (cubeRef, index) => {
        while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
          cubeRef.current.rotation.z += Math.PI / 10;
          await new Promise((resolve) => requestAnimationFrame(resolve));
          await new Promise((resolve) => setTimeout(resolve, 1));
        }
      });
    };
  
    const DraggingUp = async () => {
      cubeRefs.current.forEach(async (cubeRef, index) => {
        while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
          cubeRef.current.rotation.z -= Math.PI / 10;
          await new Promise((resolve) => requestAnimationFrame(resolve));
          await new Promise((resolve) => setTimeout(resolve, 1));
        }
      });
    };
  
    
    useEffect(() => {
      const handlePointerDown = (event) => {
        if (!groupRef.current.contains(event.target)) {
          // Clicked outside the group
          event.preventDefault();
          event.stopPropagation();
          console.log("down outside");
    
          const startX = event.clientX;
          const startY = event.clientY;
    
          const handlePointerMove = (event) => {
            console.log("move");
            const deltaX = event.clientX - startX;
            const deltaY = event.clientY - startY;
    
            groupRef.current.rotation.y += deltaX * 0.01; // Adjust the rotation speed as needed
            groupRef.current.rotation.x += deltaY * 0.01;
    
            startX = event.clientX;
            startY = event.clientY;
          };
    
          const handlePointerUp = () => {
            console.log("up");
            document.removeEventListener("pointermove", handlePointerMove);
            document.removeEventListener("pointerup", handlePointerUp);
          };
    
          document.addEventListener("pointermove", handlePointerMove);
          document.addEventListener("pointerup", handlePointerUp);
        }
      };
  
      document.addEventListener("pointerdown", handlePointerDown);
  
      return () => {
        document.removeEventListener("pointerdown", handlePointerDown);
      };
    }, []);

    return (
      <>
      
      <group ref={groupRef} scale={[0.3, 0.3, 0.3]} rotation={initialRotation} onPointerDown={handlePointerDown}>
        {nodesSubset.map((node, index) => (
          <primitive
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group>
      </>
      
    );
  };

  
export default RubiksCubeModel;
