import { useRef } from "react";
import { useFrame } from "react-three-fiber";
import * as THREE from 'three';

function LargeCube({ onFaceClick }) {
  const meshRef = useRef();

  // Use raycaster to determine which face is clicked
  const raycaster = new THREE.Raycaster();
  const mouse = new THREE.Vector2();

  const handlePointerDown = (event) => {
    // Calculate normalized mouse coordinates
    mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
    mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;

    // Set raycaster origin and direction based on mouse coordinates
    raycaster.setFromCamera(mouse, event.camera);

    // Perform ray intersection with the cube
    const intersects = raycaster.intersectObject(meshRef.current);

    if (intersects.length > 0) {
      // Get the face normal from the intersection
      const faceNormal = intersects[0].face.normal;

      // Determine which face is clicked based on the face normal
      let clickedFace = null;

      if (faceNormal.equals(new THREE.Vector3(1, 0, 0))) {
        clickedFace = "RightFace";
      } else if (faceNormal.equals(new THREE.Vector3(-1, 0, 0))) {
        clickedFace = "LeftFace";
      } else if (faceNormal.equals(new THREE.Vector3(0, 1, 0))) {
        clickedFace = "TopFace";
      } else if (faceNormal.equals(new THREE.Vector3(0, -1, 0))) {
        clickedFace = "BottomFace";
      } else if (faceNormal.equals(new THREE.Vector3(0, 0, 1))) {
        clickedFace = "FrontFace";
      } else if (faceNormal.equals(new THREE.Vector3(0, 0, -1))) {
        clickedFace = "BackFace";
      }

      // Invoke the callback with the clicked face
      if (clickedFace) {
        onFaceClick(clickedFace);
      }
    }
  };

  // Update the raycaster on each frame
  useFrame(({ gl, camera }) => {
    raycaster.update();
  });

  return (
    <mesh
      ref={meshRef}
      rotation={[Math.PI / 6, Math.PI / 4, 0]}
      onPointerDown={handlePointerDown}
    >
      <boxGeometry attach="geometry" args={[1.8, 1.8, 1.8]} />
      <meshStandardMaterial attach="material" color="#6be092" wireframe={true} />
    </mesh>
  );
}
export default LargeCube;
