import React, { useRef,useState } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame,useThree } from "react-three-fiber";
import { Vector2, Raycaster } from "three";
import * as THREE from 'three';

const RubiksCubeModel = ({ nodesSubset, drag, onDragChange }) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  const groupRef = useRef();

let startY,endY,startX,endX,startZ,endZ;
  let mousepRefX = useRef(0);
  let mousepRefY = useRef(0);
  let mousepRefZ = useRef(0);
  const handlePointerDown = (event) => {

startX=mousepRefX.current;
startY=mousepRefY.current;
startZ=mousepRefZ.current;
console.log("startX "+startX);
          console.log("startX "+startY);
          console.log("startX "+startZ);
// console.log("start "+startY);
    onDragChange(false);
    window.addEventListener("pointermove", handlePointerMove, { passive: true });
    window.addEventListener("pointerup", handlePointerUp, { passive: true });
    event.stopPropagation();
  };

  let deltaY;



  const handlePointerMove = (event) => {
    
      }
  let targetRot = 0;

  const handlePointerUp = async () => {
    window.removeEventListener("pointermove", handlePointerMove);
    window.removeEventListener("pointerup", handlePointerUp);



        if (startY !== null) {
          endX=mousepRefX.current;
          endY=mousepRefY.current;
          endZ=mousepRefZ.current;
          console.log("endX "+endX);
          console.log("endY "+endY);
          console.log("endZ "+endZ);
        }

        
    if (startY > endY) {
      console.log(startY+" "+endY)
      cubeRefs.current.forEach((cubeRef) => {
        targetRot = cubeRef.current.rotation.z + Math.PI / 2;
      });
      DraggingDown();
      console.log("up");
      deltaY = 0;
    } else if (startY < endY) {
      cubeRefs.current.forEach((cubeRef) => {
        targetRot = cubeRef.current.rotation.z - Math.PI / 2;
      });
      DraggingUp();
      console.log("up");
      deltaY = 0;
    }
    onDragChange(true);
  };

  const DraggingDown = async () => {
    cubeRefs.current.forEach(async (cubeRef) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z += Math.PI / 10;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
    onDragChange(true);
  };

  const DraggingUp = async () => {
    cubeRefs.current.forEach(async (cubeRef) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z -= Math.PI / 10;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
    onDragChange(true);
  };

  useFrame((state) => {
    // console.log(state.mouse.y);
    mousepRefY.current = state.mouse.y; 
    mousepRefX.current = state.mouse.x; 
    mousepRefZ.current = state.mouse.z; 
    orbitRef.current.enabled = drag;
    orbitRef.current.update();
  });

  return (
    <>
      <group classname="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
      >
        {nodesSubset.map((node, index) => (
          <primitive
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group>
      <OrbitControls ref={orbitRef}  />
       <axesHelper args={[5]} />
    </>
  );
};

export default RubiksCubeModel;
