import React, { useRef,useEffect} from "react";
import {useFrame } from "react-three-fiber";

const RubiksCubeModel = ({ nodesSubset }) => {
    const cubeRefs = useRef([]);
    const initialRotation = [Math.PI / 6, Math.PI / 4, 0];

    useEffect(() => {
      cubeRefs.current.forEach((cubeRef, index) => {
        cubeRef.current.rotation.set(...initialRotation);
      });
    }, [initialRotation]);

    return (
      <group scale={[0.3, 0.3, 0.3]} rotation={initialRotation}>
        {nodesSubset.map((node, index) => (
          <mesh
          key={index}
          ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          onClick={() => {
            cubeRefs.current[index].current.rotation.x += Math.PI / 2;
            cubeRefs.current[index].current.rotation.y += Math.PI / 2;
            cubeRefs.current[index].current.rotation.z += Math.PI / 2;
          }}
        >
          <primitive object={node} />
        </mesh>
        ))}
      </group>
    );
  };

  
export default RubiksCubeModel;
