import React, { useEffect, useRef } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame } from "react-three-fiber";
import * as THREE from 'three';
import { Tween } from 'tween.js';
import * as TWEEN from 'tween.js';


const RubiksCubeModel = ({
  index,
  nodesSubset,
  drag,
  onDragChange,
  mouseDownPlane,
  mouseUpPlane,
  mouseDownCube,
  set,
  xyzdirection,
  direction,
}) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  // const initialRotation = [Math.PI / 6, Math.PI/4, 0];
  const initialRotation = [0, 0, 0];
  const groupRef = useRef();
  let rotationindex;

  let checkup;
  const handlePointerDown = (event) => {
    const clickedPlane = groupRef.current.children[0].children.find(
      (child) => child === event.object
    );
    if (clickedPlane) {
      mouseDownPlane(clickedPlane.name);
      mouseDownCube(index);
    }
    checkup = 1;
    onDragChange(false);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  };

  useEffect(() => {
    rotationindex = set;
    Dragging(rotationindex);
  }, [set]);

  const handlePointerUp = async (event) => {
    if (checkup == null) {
      const clickedPlane = groupRef.current.children[0].children.find(
        (child) => child === event.object
      );
      if (clickedPlane) {
        mouseUpPlane(clickedPlane.name);
      }
      window.removeEventListener("pointerup", handlePointerUp);
      event.stopPropagation();
    }
    onDragChange(true);
  };
let rotationGroup;
const cubesGroup = new THREE.Group();

  const Dragging = async (cubeIndices) => {
    if (cubeIndices != null) {
      cubeIndices.forEach((cubeIndex) => {
        if (cubeRefs.current[cubeIndex]) {
          cubesGroup.add(cubeRefs.current[cubeIndex]);
        }
      });
      if (direction === "a") {
        await Up(cubesGroup);
      } else if (direction === "c") {
        await Down(cubesGroup);
      }
      // if (cubeIndices.includes(index)) {
      //   if (direction === "a") {
      //     Up();  
      //   } else if(direction === "c"){
      //     Down();
      //     console.log("down");
      //   }
      // }
    }
  };


  const animateRotation = (target, endRotation, duration) => {
    return new Promise((resolve) => {
      new TWEEN.Tween(target.rotation)
        .to(endRotation, duration)
        .onComplete(() => resolve())
        .start();
    });
  };



  const Up = async () => {
    console.log("down");

    let initialRotation = groupRef.current.rotation.clone();
  
    if (xyzdirection === "x") {
      for (let i = 0; i < 15; i++) {
        initialRotation.x -= Math.PI / 30;
        await animateRotation(groupRef.current, { x: initialRotation.x, y: initialRotation.y, z: initialRotation.z }, 100);
        console.log(i);
      }
      console.log(groupRef.current.rotation);
    } else if (xyzdirection === "y") {
      for (let i = 0; i < 15; i++) {
        // Adjust the initial rotation in each iteration
        initialRotation.y -= Math.PI / 30;
        await animateRotation(groupRef.current, { x: initialRotation.x, y: initialRotation.y, z: initialRotation.z }, 100);
      }
    } else if (xyzdirection === "z") {
      for (let i = 0; i < 15; i++) {
        // Adjust the initial rotation in each iteration
        initialRotation.z -= Math.PI / 30;
        await animateRotation(groupRef.current, { x: initialRotation.x, y: initialRotation.y, z: initialRotation.z }, 100);
      }
    }
  };
  



  const Down = async () => {
    let initialRotation = groupRef.current.rotation.clone(); 
    if (xyzdirection === "x") {
      for (let i = 0; i < 15; i++) {
        initialRotation.x += Math.PI / 30;
      await animateRotation(groupRef.current, { x: initialRotation.x, y: initialRotation.y, z: initialRotation.z }, 100);
        console.log(i);
      }
      console.log(groupRef.current.rotation);
    } else if (xyzdirection === "y") {
      for (let i = 0; i < 15; i++) {
        initialRotation.y += Math.PI / 30;
      await animateRotation(groupRef.current, { x: initialRotation.x, y: initialRotation.y, z: initialRotation.z }, 100);
      }
    } else if (xyzdirection === "z") {
      for (let i = 0; i < 15; i++) {
        initialRotation.z += Math.PI / 30;
      await animateRotation(groupRef.current, { x: initialRotation.x, y: initialRotation.y, z: initialRotation.z }, 100);
      }
    }
  };
  
  
  useFrame(() => {
    orbitRef.current.enabled = drag;
    orbitRef.current.update();
    TWEEN.update()
  });

  return (
    <>
      <group
        className="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
        onPointerUp={handlePointerUp}
      >
        {nodesSubset.map((node, nodeIndex) => (
          <primitive
            key={nodeIndex}
            object={node}
            ref={(ref) => (cubeRefs.current[nodeIndex] = ref)} 
          />
        ))}
      </group>
      <OrbitControls ref={orbitRef} enableZoom={false} />
      <axesHelper args={[5]} />
    </>
  );
};

export default RubiksCubeModel;
