import React, { Suspense, useMemo } from 'react';
import { Canvas } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader, useFrame } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';

const RubiksCube = () => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');

  const nodesSubset1 = useMemo(() => scene.children.slice(0, 9), [scene]);
  const nodesSubset2 = useMemo(() => scene.children.slice(9), [scene]);

  return (
    <Canvas>
      <ambientLight />
      <OrbitControls enableZoom={false} />
      <Suspense fallback={null}>
        <group position={[2, 0, 0]}>
          <RubiksCubeModel nodesSubset={nodesSubset1} />
        </group>
        <group position={[2, 0, 0]}>
          <RubiksCubeModel nodesSubset={nodesSubset2} />
        </group>
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

const RubiksCubeModel = ({ nodesSubset }) => {
  return (
    <group scale={[0.2, 0.2, 0.2]}>
      <RotatingGroup>
        {nodesSubset.map((node, index) => (
          <primitive key={index} object={node} />
        ))}
      </RotatingGroup>
    </group>
  );
};

const RotatingGroup = ({ children }) => {
  const groupRef = useFrame(({ clock }) => {
    if (groupRef.current) {
      groupRef.current.rotation.x = Math.sin(clock.elapsedTime) * 0.2; // Adjust the rotation speed and range as needed
      groupRef.current.rotation.y = Math.cos(clock.elapsedTime) * 0.2;
    }
  });

  return <group ref={groupRef}>{children}</group>;
};

export default RubiksCube;
