import React, { Suspense, useRef } from 'react';
import { Canvas, useFrame } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';

const RotatableSet = ({ nodesSubset }) => {
  const setGroup = useRef();

  // Use useFrame to apply rotations
  useFrame(() => {
    // Rotate the entire group
    setGroup.current.rotation.y += 0.005; // Adjust rotation speed as needed
  });

  return (
    <group ref={setGroup}>
      {nodesSubset.map((node, index) => (
        <mesh key={index} position={[node.translation[0], node.translation[1], node.translation[2]]}>
          <primitive object={node.mesh} />
        </mesh>
      ))}
    </group>
  );
};

const RubiksCubeModel = () => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');

  // Check if scene or nodes is not defined
  if (!scene || !scene.nodes) {
    console.error('Model not loaded correctly.');
    return null;
  }

  const firstSet = scene.nodes.slice(0, 9); // Adjust indices based on your model structure
  const secondSet = scene.nodes.slice(9, 18);

  return (
    <group>
      <RotatableSet nodesSubset={firstSet} />
      <RotatableSet nodesSubset={secondSet} />
    </group>
  );
};

const RubiksCube = () => {
  return (
    <Canvas>
      <ambientLight />
      <Suspense fallback={null}>
        <RubiksCubeModel />
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

export default RubiksCube;
