import React, { Suspense, useMemo,useRef,useState } from 'react';
import { Canvas ,useFrame} from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';

const RubiksCube = () => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  scene.rotation.set(...initialRotation);

  const nodesSubset1 = useMemo(() => scene.children.slice(0, 9), [scene]);//only one layer
  const nodesSubset2 = useMemo(() => scene.children.slice(9), [scene]);//two layers

  const [rotationSubset1, setRotationSubset1] = useState(initialRotation);
  const [rotationSubset2, setRotationSubset2] = useState(initialRotation);

  return (
    <Canvas>
      <ambientLight />
      {/* <OrbitControls enableZoom={false} /> */}
      <Suspense fallback={null}>
        <group position={[0, 0, 0]} rotation={rotationSubset1}>
          <RubiksCubeModel nodesSubset={nodesSubset1} rotation={rotationSubset1} setRotation={setRotationSubset1}/>
        </group>
        <group position={[0, 0, 0]} rotation={rotationSubset1}>
          <RubiksCubeModel nodesSubset={nodesSubset2} rotation={rotationSubset2} setRotation={setRotationSubset2}/>
        </group>
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

const RubiksCubeModel = ({ nodesSubset }) => {
  const cubeRefs = useRef([]);
  const dragging = useRef(false);
  const initialMousePosition = useRef({ x: 0, y: 0 });

  const handlePointerDown = (event) => {
    dragging.current = true;
    initialMousePosition.current = { x: event.clientX, y: event.clientY };
  };

  const handlePointerMove = (event) => {
    if (dragging.current) {
      const deltaX = event.clientX - initialMousePosition.current.x;
      const deltaY = event.clientY - initialMousePosition.current.y;

      // Adjust the rotation based on the mouse movement
      cubeRef.current.rotation.y += deltaX * 0.01;
      cubeRef.current.rotation.x += deltaY * 0.01;

      initialMousePosition.current = { x: event.clientX, y: event.clientY };
    }
  };

  const handlePointerUp = () => {
    dragging.current = false;
  };


  useFrame(() => {
    if (isDragging) {
      cubeRefs.current.forEach((cubeRef) => {
        // Rotate all cubes in the subset when any one of them is being dragged
        cubeRef.current.rotation.y += 0.01;
      });
    }
  });

  return (
    <group scale={[0.3, 0.3, 0.3]} onPointerDown={handlePointerDown} onPointerUp={handlePointerUp} onPointerMove={handlePointerMove}>
      {nodesSubset.map((node, index) => (
        <primitive
          key={index}
          object={node}
          ref={(ref) => (cubeRefs.current[index] = { current: ref, isDragging: false })}

        />
      ))}
    </group>
  );
};

export default RubiksCube;
