import React, { Suspense, useMemo, useRef, useState } from 'react';
import { Canvas, useFrame } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';

const RubiksCube = () => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  scene.rotation.set(...initialRotation);

  const nodesSubset1 = useMemo(() => scene.children.slice(0, 9), [scene]); // only one layer
  const nodesSubset2 = useMemo(() => scene.children.slice(9), [scene]); // two layers

  const [rotationSubset1, setRotationSubset1] = useState(initialRotation);
  const [rotationSubset2, setRotationSubset2] = useState(initialRotation);

  useFrame(() => {
    // Update the rotation directly in the useFrame hook
    scene.children.slice(0, 9).forEach((child, index) => {
      child.rotation.set(...rotationSubset1);
    });

    scene.children.slice(9).forEach((child, index) => {
      child.rotation.set(...rotationSubset2);
    });
  });

  return (
    <Canvas>
      <ambientLight />
      <OrbitControls enableZoom={false} />
      <Suspense fallback={null}>
        <group position={[0, 0, 0]} rotation={initialRotation}>
          <RubiksCubeModel nodesSubset={nodesSubset1} />
        </group>
        <group position={[0, 0, 0]} rotation={initialRotation}>
          <RubiksCubeModel nodesSubset={nodesSubset2} />
        </group>
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

const RubiksCubeModel = ({ nodesSubset }) => {
  return (
    <group scale={[0.3, 0.3, 0.3]}>
      {nodesSubset.map((node, index) => (
        <primitive key={index} object={node} />
      ))}
    </group>
  );
};

export default RubiksCube;
