import { Canvas, useFrame } from '@react-three/fiber'
import { Stats, OrbitControls, RoundedBox, Environment } from '@react-three/drei'
// import { useControls, button } from 'leva'
import { Suspense, useRef } from 'react'
import { Color } from 'three'
import TWEEN from '@tweenjs/tween.js'

function Buttons({ cubeGroup }) {
  const rotationGroup = useRef()

  useControls('Cube', {
    'Left CW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'x', -0.5, 1)
    }),
    'Left CCW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'x', -0.5, -1)
    }),
    'Right CW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'x', 0.5, -1)
    }),
    'Right CCW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'x', 0.5, 1)
    }),
    'Back CW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'z', -0.5, 1)
    }),
    'Back CCW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'z', -0.5, -1)
    }),
    'Front CW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'z', 0.5, -1)
    }),
    'Front CCW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'z', 0.5, 1)
    }),
    'Top CW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'y', 0.5, -1)
    }),
    'Top CCW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'y', 0.5, 1)
    }),
    'Bottom CW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'y', -0.5, 1)
    }),
    'Bottom CCW': button(() => {
      rotate(cubeGroup.current, rotationGroup.current, 'y', -0.5, -1)
    })
  })

  return (
    <>
      <group ref={rotationGroup} />
    </>
  )
}

function Cube() {
  const ref = useRef()

  useFrame(() => {
    TWEEN.update()
  })

  return (
    <>
      <group ref={ref}>
        {[...Array(3).keys()].map((x) =>
          [...Array(3).keys()].map((y) =>
            [...Array(3).keys()].map((z) => <Cubelet key={x + y * 3 + z * 9} position={[x - 1, y - 1, z - 1]} />)
          )
        )}
      </group>
      <Buttons cubeGroup={ref} />
    </>
  )
}

function Cubelet({ position }) {
  return (
    <RoundedBox position={position}>
      <meshStandardMaterial color={new Color(Math.floor(Math.random() * 16777216))} />
    </RoundedBox>
  )
}

function resetCubeGroup(cubeGroup, rotationGroup) {
  rotationGroup.children
    .slice()
    .reverse()
    .forEach(function (c) {
      cubeGroup.attach(c)
    })
  rotationGroup.quaternion.set(0, 0, 0, 1)
}

function attachToRotationGroup(cubeGroup, rotationGroup, axis, limit) {
  cubeGroup.children
    .slice()
    .reverse()
    .filter(function (c) {
      return limit < 0 ? c.position[axis] < limit : c.position[axis] > limit
    })
    .forEach(function (c) {
      rotationGroup.attach(c)
    })
}

function animateRotationGroup(rotationGroup, axis, multiplier) {
  new TWEEN.Tween(rotationGroup.rotation)
    .to(
      {
        [axis]: rotationGroup.rotation[axis] + (Math.PI / 2) * multiplier
      },
      250
    )
    .easing(TWEEN.Easing.Cubic.InOut)
    .start()
}

function rotate(cubeGroup, rotationGroup, axis, limit, multiplier) {
  if (!TWEEN.getAll().length) {
    resetCubeGroup(cubeGroup, rotationGroup)
    attachToRotationGroup(cubeGroup, rotationGroup, axis, limit)
    animateRotationGroup(rotationGroup, axis, multiplier)
  }
}

export default function App() {
  return (
    <>
      <Canvas camera={{ position: [3, 3, 3] }}>
        <Suspense>
          <Environment preset="forest" />
        </Suspense>
        <Cube />
        <OrbitControls target={[0.75, 0, 0]} />
        <Stats />
      </Canvas>
      <div id="instructions">
        See{' '}
        <a href="https://sbcode.net/react-three-fiber/rubiks-cube/" target="_blank" rel="nofollow noreferrer">
          Possible Cube
        </a>{' '}
        version
      </div>
    </>
  )
}
