import React, { useRef } from "react";
import { OrbitControls } from "@react-three/drei";
import { useFrame } from "react-three-fiber";

const RubiksCubeModel = ({ index,nodesSubset, drag, onDragChange }) => {
  const cubeRefs = useRef([]);
  const orbitRef = useRef();
  const startYRef = useRef(0);
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  const groupRef = useRef();


  const handlePointerDown = (event) => {
    console.log("Clicked on Node" + index);
    startYRef.current = event.clientY;
    onDragChange(false);
  
    const handleMove = (moveEvent) => {
      // handle pointer move logic
    };
  
    const handleUp = (upEvent) => {
      console.log("Up on Node" + index);
      window.removeEventListener("pointermove", handleMove);
      window.removeEventListener("pointerup", handleUp);
      onDragChange(true);
      upEvent.stopPropagation();
    };
  
    window.addEventListener("pointermove", handleMove, { once: true, passive: true });
    window.addEventListener("pointerup", handleUp, { once: true, passive: true });
  
    event.stopPropagation();
  };
  

  useFrame(() => {
    orbitRef.current.enabled = drag;
    orbitRef.current.update();
  });


  
  return (
    <>
      <group classname="group"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
        onPointerUp={handleUp}
      >
        {nodesSubset.map((node, index) => (
          <primitive
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group>
      <group>

      </group>
      <OrbitControls ref={orbitRef} enableZoom={false}  />
    </>
  );
};

export default RubiksCubeModel;
