import React, { useRef, useState, useEffect } from "react";
import { OrbitControls } from "@react-three/drei";
import { Canvas, useFrame } from "@react-three/fiber";

const Cube = ({ onCubeClick }) => {
  const cubeRef = useRef();

  // This will rotate the cube on the Y-axis every frame
  useFrame(() => {
    cubeRef.current.rotation.y += 0.01;
  });

  return (
    <mesh
      ref={cubeRef}
      onClick={onCubeClick} // Add onClick event to handle cube click
    >
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color="orange" />
    </mesh>
  );
};

const RubiksCubeModel = () => {
  const controlsRef = useRef();
  const [enableOrbitControls, setEnableOrbitControls] = useState(true);
  const [interactionCount, setInteractionCount] = useState(0);

  const handleCubeClick = () => {
    setInteractionCount((prevCount) => prevCount + 1);
  };

  useEffect(() => {
    if (interactionCount >= 3) {
      setEnableOrbitControls(false);
    }
  }, [interactionCount]);

  return (
    <>
      <Cube onCubeClick={handleCubeClick} />
      {enableOrbitControls && <OrbitControls ref={controlsRef} enableZoom={false} />}
    </>
  );
};

export default RubiksCubeModel;
