import React, { useMemo,Suspense,useRef, useEffect, useState } from "react";
// import { OrbitControls } from "@react-three/drei";
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { Canvas, useThree } from "react-three-fiber";
import * as THREE from "three";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader";
import { useLoader } from "react-three-fiber";

const RubiksCubeModel = ({ nodesSubset }) => {

  const cubeRefs = useRef([]);
  const startYRef = useRef(0);
  const initialRotation = [Math.PI / 6, Math.PI / 4, 0];
  const groupRef = useRef();



  const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
camera.position.set(10, 8, 10);

const renderer = new THREE.WebGLRenderer({ canvas: document.querySelector("#canvas") });
renderer.setPixelRatio(window.devicePixelRatio);
renderer.setSize(window.innerWidth, window.innerHeight);

const raycaster = new THREE.Raycaster();
const pointer = new THREE.Vector2();

const radStep = Math.PI / 2 / 50;

const controls = new OrbitControls(camera, renderer.domElement);
controls.enabled = false;

const pivot = new THREE.Object3D();

let pan = {
  isPanning: false,
  mouseX: 0,
  mouseY: 0
};

let draggable = {
  isDragging: false,
  cube: null,
  direction: null,
  position: null,
  mouseX: 0,
  mouseY: 0,
};



  const handlePointerDown = (event) => {
    // console.log("down");
    // startYRef.current = event.clientY;




    pointer.x = (event.clientX / window.innerWidth) * 2 - 1;
    pointer.y = - (event.clientY / window.innerHeight) * 2 + 1;
    const cubes = cubeRefs.current.map((refObj) => refObj.current);
    raycaster.setFromCamera(pointer, camera);
    const intersects = raycaster.intersectObjects(cubes, false);

    // if (draggable.intersect == null) {
    //     draggable.intersect = intersects[0];
    // }

    if (intersects.length > 0) {
        controls.enabled = false;

        draggable.isDragging = true;
        draggable.mouseX = event.clientX;
        draggable.mouseY = event.clientY;

        draggable.cube = intersects[0].object;

        const intersectPoint = new THREE.Vector3(...intersects[0].point.toArray().map(c => +(c.toFixed(3))));
        const objPos = new THREE.Vector3(...intersects[0].object.position.toArray().map(c => +(c.toFixed(3))));

        // if (intersectPoint.z == objPos.z + 1) {
        //     draggable.direction = "front";
        // }
        // else if (intersectPoint.y == objPos.y + 1) {
        //     draggable.direction = "top";
        // }
        // else if (intersectPoint.x == objPos.x + 1) {
        //     draggable.direction = "side";
        // }
    }
    else if(!controls.enabled){
        pan.isPanning = true;
        pan.mouseX = event.clientX;
        pan.mouseY = event.clientY;
    }



    window.addEventListener("pointermove", handlePointerMove);
    window.addEventListener("pointerup", handlePointerUp);
    event.stopPropagation();
  };

  let deltaY;

  const handlePointerMove = (event) => {
    console.log("move");
    const startY = startYRef.current;
    if (startY !== null) {
      deltaY = event.clientY - startY;
    }
  };

  let targetRot = 0;

  const handlePointerUp = async () => {
    draggable.isDragging = false;
    pan.isPanning = false;
    console.log("up");
    window.removeEventListener("pointermove", handlePointerMove);
    window.removeEventListener("pointerup", handlePointerUp);
    if (deltaY > 0) {
      cubeRefs.current.forEach((cubeRef, index) => {
        targetRot = cubeRef.current.rotation.z + Math.PI / 2;
      });

      DraggingDown();
      deltaY = 0;
    } else if (deltaY < 0) {
      cubeRefs.current.forEach((cubeRef, index) => {
        targetRot = cubeRef.current.rotation.z - Math.PI / 2;
        console.log(cubeRef.current.rotation.z);
      });

      DraggingUp();
      deltaY = 0;
    }
  };

  const DraggingDown = async () => {
    cubeRefs.current.forEach(async (cubeRef, index) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z += Math.PI / 10;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  const DraggingUp = async () => {
    cubeRefs.current.forEach(async (cubeRef, index) => {
      while (Math.abs(cubeRef.current.rotation.z - targetRot) >= 0.001) {
        cubeRef.current.rotation.z -= Math.PI / 10;
        await new Promise((resolve) => requestAnimationFrame(resolve));
        await new Promise((resolve) => setTimeout(resolve, 1));
      }
    });
  };

  return (
    <>

      <group className="model"
        ref={groupRef}
        scale={[0.3, 0.3, 0.3]}
        rotation={initialRotation}
        onPointerDown={handlePointerDown}
      >
        {nodesSubset.map((node, index) => (
          <primitive
          className="model"
            key={index}
            object={node}
            ref={(ref) => (cubeRefs.current[index] = { current: ref })}
          />
        ))}
      </group> 

    </>
  );
};

export default RubiksCubeModel;
