import React, { useRef } from 'react';
import { Canvas, useFrame } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';

const RubiksCube = () => {
  return (
    <Canvas>
      <ambientLight />
      <pointLight position={[10, 10, 10]} />
      <RubiksCubeModel position={[0, 0, 0]} />
    </Canvas>
  );
};

const RubiksCubeModel = ({ position }) => {
  const group = useRef();
  const { nodes } = useLoader(GLTFLoader, '/path/to/your/rubiks-cube.gltf');

  useFrame(() => {
    // Update cube rotations here based on mouse input
  });

  return (
    <group ref={group} position={position}>
      <RubiksCubeFaces />
    </group>
  );
};

const RubiksCubeFaces = () => {
  return <primitive object={nodes.Scene} />;
};

export default RubiksCube;
