import React, { Suspense } from 'react';
import { Canvas, useFrame } from 'react-three-fiber';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader';
import { useLoader } from 'react-three-fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';

const RubiksCube = () => {
  return (
    <Canvas>
      <ambientLight />
      <OrbitControls enableZoom={false} />
      <Suspense fallback={null}>
        <RubiksCubeModel />
      </Suspense>
      <pointLight position={[10, 10, 10]} />
    </Canvas>
  );
};

const RubiksCubeModel = () => {
  const { scene } = useLoader(GLTFLoader, '/rubikcube.gltf');
  scene.scale.set(0.3, 0.3, 0.3);
  scene.rotation.set(Math.PI / 6, Math.PI / 4, 0);

  // Create a hierarchy of Group objects for each layer
  const layers = [];

  let currentIndex = 0;

  for (let i = 0; i < 3; i++) {
    for (let j = 0; j < 3; j++) {
      for (let k = 0; k < 3; k++) {
        const cube = scene.children[currentIndex];
        if (!layers[i]) {
          layers[i] = new THREE.Group(); // Create a new group for each layer
          scene.add(layers[i]);
        }

        cube.position.set((i - 1) * 1.1, (j - 1) * 1.1, (k - 1) * 1.1);
        layers[i].add(cube);
        currentIndex++;
      }
    }
  }

  // Rotate only the second layer (adjust as needed)
  useFrame(() => {
    layers[1].rotation.y += 0.01; // Adjust rotation speed as needed
  });

  return null; // We don't return anything because the cubes are added to the scene directly
};

export default RubiksCube;
